import React, { useState } from 'react'
import styled from 'styled-components'
import { COLORS } from '../Colors.js'

function Header() {
    const [click, setClick] = useState(false);
    const handleClick = () => setClick(!click)
    return (
        <Container>
            <Logo>
                <a href="/" >
                    <img src="logo.png" />
                </a>
            </Logo>
            <Search>
                <input type="text" placeholder="Search by product or location"/>
                <SearchIcon>
                    <img src="images/search-icon.svg"/>
                </SearchIcon>
            </Search>
            <Nav>
                <NavOpt><a href="/singup">Sign Up</a></NavOpt>
                <NavOpt><a href="/login">Log In</a></NavOpt>
                <NavOpt><a href="/checkout">Checkout</a></NavOpt>
            </Nav>
            <Menu onClick={handleClick}>
                <img src={click ? 'bars.svg' : 'times.svg'}/>
            </Menu>
        </Container>
    );
}

export default Header

const Container = styled.div`
    height: 70px;
    background: ${COLORS.mantis};
    display: flex;
    align-items: center;
    justify-content: space-between;
`

const Logo = styled.div`
    margin: 10px;
    height: 100%;
    a {
        img {
            max-height: 100%;
        }
    }
`

const Search = styled.div`
    position: relative;
    flex-grow: 1;
    min-width: 320px;
    max-width: 600px;
    input {
        width: 100%;
        opacity: 1;
        background-color: white;
        border: none;
        border-radius: 3px;
        height: 45px;
        padding-left: 10px;
        font-size; 40px;
        line-height: 3;
    }
`

const SearchIcon = styled.div`
    z-index: 1;
    position: absolute;
    top: 10px;
    right: 0px;
    cursor: text;
`

const Nav = styled.div`
    display: flex;
    margin: 20px;
`

const NavOpt = styled.div`
    margin-left: 25px;
    border: 2px;
    border-color: black;
    a {
        font-size: 20px;
        text-decoration: none;
        white-space: nowrap;
        color: black;
    }
`

const Menu = styled.div`

`
