import React, { useState } from 'react'
import styled from 'styled-components'
import { COLORS } from '../Colors.js'
import { FaBars, FaTimes, FaShoppingCart } from 'react-icons/fa'

function Header() {
    const [click, setClick] = useState(false);
    const handleClick = () => setClick(!click)

    const [menu, setMenu] = useState(false);
    const toggleMenu = () => setMenu(!menu)
    //window.addEventListener('resize', toggleMenu);

    return (
        <Container>
            <Main>
                <Logo href="/" >
                    <img src="logo.png" />
                    <div>Company Name</div>
                </Logo>
                <Search>
                    <input type="text" placeholder=" Search by product or location"/>
                    <img src="images/search-icon.svg"/>
                </Search>
                <Nav>
                    <a className="login" href="/login">Log In/Sign Up</a>
                    <a href="/checkout"><FaShoppingCart/></a>
                    { menu ? ( <FaTimes onClick={toggleMenu}/> ) : ( <FaBars onClick={toggleMenu}/> )}
                </Nav>
            </Main>
            <Menu>
                <ul>
                    <li>about</li>
                    <li>services</li>
                    <li>nutrition calculator</li>
                    <li>something something</li>
                </ul>
            </Menu>
        </Container>
    );
}

export default Header

const Container = styled.div`
    font-size: 20px;
`
const Main = styled.div`
    height: 40px;
    background: ${COLORS.mantis};
    display: flex;
    align-items: center;
    justify-content: space-between;
`

const Logo = styled.a`
    display: flex;
    align-items: center;
    text-decoration: none;
    margin: 10px;
    height: 100%;
    img {
        max-height: 100%;
        margin-right: 5px;
    }
    div {
        white-space: nowrap;
        color: black;
    }
`

const Search = styled.div`
    position: relative;
    flex-grow: 1;
    min-width: 320px;
    max-width: 600px;
    height: 30px;
    input {
        height: 30px;
        width: 100%;
        opacity: 1;
        border: none;
        border-radius: 3px;
        line-height: 3;
    }
    img {
        position: absolute;
        right: 0px;
        top: 2.5px;
        z-index: 1;
        cursor: text;
    }
`

const Nav = styled.nav`
    display: flex;
    align-items: center;
    > * {
        margin-right: 12px;
        margin-left: 12px;
        color: black;
    }
    a.login {
        font-size: 12px;
        text-decoration: none;
        white-space: nowrap;
        padding: 3px 8px 3px 8px;
        border: solid;
        border-width: 1px;
        border-radius: 4px;
        border-color: black;
    }
    svg {
        font-size: 20px;
        vertical-align: bottom;
        height: 25px;
        cursor: pointer;
    }
`

const Menu = styled.div`
    width: 450px;
    background: ${COLORS.mantis};
    height: 100vh;
    display: flex;
    justify-content: center;
    position: fixed;
    top: 40px;
    right: ${({ menu }) => (menu ? '0' : '-100%')};
    transition: 350ms;
    z-index: 10;
`
