import React from 'react'
import styled from 'styled-components'
import { COLORS } from './Colors.js'

function Header() {
    return (
        <Container>
            <a href="/">
                <Logo src="logo.png" />
            </a>
            <Search>
                <input type="text" placeholder="Search by product or location"/>
                <SearchIcon>
                    <img src="images/search-icon.svg"/>
                </SearchIcon>
            </Search>
            <Nav>
                <NavOpt><a href="/singup">Sign Up</a></NavOpt>
                <NavOpt><a href="/login">Log In</a></NavOpt>
                <NavOpt><a href="/checkout">Checkout</a></NavOpt>
            </Nav>
        </Container>
    );
}

export default Header

const Container = styled.div`
    height: 70px;
    background: ${COLORS.mantis};
    display: flex;
    align-items: center;
`

const Logo = styled.img`
    width: 70px;
`

const Search = styled.div`
    flex-grow: 1;
    margin: 20px;
    position: relative;
    input {
        opacity: 1;
        background-color: white;
        border: none;
        border-radius: 3px;
        height: 45px;
        min-width: 200px;
        max-width: 400px;
        width: 400px;
        padding: 0 8px 0 40px;
        font-size; 40px;
        line-height: 3;
    }
`

const SearchIcon = styled.div`
    z-index: 1;
    position: absolute;
    top: 10px;
    left: 5px;
`

const Nav = styled.div`
    display: flex;
    align-items: center;
`

const NavOpt = styled.div`
    margin: 20px;
    a {
        text-decoration: none;
        color: black;
    }
`
