import React, { useState } from 'react'
import styled from 'styled-components'
import { COLORS } from '../Colors.js'

function Header() {
    const [click, setClick] = useState(false);
    const handleClick = () => setClick(!click)
    const [menu, setMenu] = useState(false);
    const showMenu = () => {
        if (window.innerWidth <= 800) {
            setMenu(true);
        } else {
            setMenu(false);
        }
    };
    window.addEventListener('resize', showMenu);

    return (
        <Container>
            <Logo href="/" >
                <img src="logo.png" />
                <div>Company Name</div>
            </Logo>
            <Search>
                <input type="text" placeholder=" Search by product or location"/>
                <img src="images/search-icon.svg"/>
            </Search>
            <Nav>
                <a href="/singup">Sign Up</a>
                <a href="/login">Log In</a>
                <a href="/checkout">Checkout</a>
                <img onClick={handleClick} src={click ? 'times.svg' : 'bars.svg'}/>
            </Nav>
        </Container>
    );
}

export default Header

const Container = styled.div`
    font-size: 20px;
    height: 40px;
    background: ${COLORS.mantis};
    display: flex;
    align-items: center;
    justify-content: space-between;
`

const Logo = styled.a`
    display: flex;
    align-items: center;
    text-decoration: none;
    margin: 10px;
    height: 100%;
    img {
        max-height: 100%;
        margin-right: 5px;
    }
    div {
        white-space: nowrap;
        color: black;
    }
`

const Search = styled.div`
    position: relative;
    flex-grow: 1;
    min-width: 320px;
    max-width: 600px;
    height: 30px;
    input {
        height: 30px;
        width: 100%;
        opacity: 1;
        border: none;
        border-radius: 3px;
        line-height: 3;
    }
    img {
        position: absolute;
        right: 0px;
        top: 2.5px;
        z-index: 1;
        cursor: text;
    }
`

const Nav = styled.div`
    display: flex;
    border-color: black;
    align-items: center;
    a {
        margin-left: 25px;
        text-decoration: none;
        white-space: nowrap;
        color: black;
    }
    img {
        margin-left: 25px;
        margin-right: 15px;
        height: 20px;
        padding-top: 2.5px;
        padding-bottom: 2.5px;
    }
    * {

        padding: 3px 8px 3px 8px;
        border: solid;
        border-width: 1px;
    }
`

const Menu = styled.div`
`
