import React, { useState } from 'react'
import styled from 'styled-components'
import { COLORS } from '../Colors.js'
import { FaBars, FaTimes, FaShoppingCart, FaSearch } from 'react-icons/fa'

function Header() {
    const [menu, setMenu] = useState(false);
    const toggleMenu = () => setMenu(!menu)

    const [main, setMain] = useState(false);
    const showMain = () => {
        if (window.innerWidth <= 768) {
            setMain(false)
        } else {
            setMain(true)
        }
    }
    window.addEventListener('resize', showMain);

    return (
        <Container>
            <Main>
                <Logo href="/" >
                    <img src="logo.png" />
                    <div>Dingus Foods</div>
                </Logo>
                <Search>
                    <input type="text" placeholder=" Produce or Location"/>
                    <FaSearch/>
                </Search>
                <Nav>
                    <a className="login" href="/login">Log In/Sign Up</a>
                    <a href="/checkout"><FaShoppingCart/></a>
                    { menu ? ( <FaTimes onClick={toggleMenu}/> ) : ( <FaBars onClick={toggleMenu}/> )}
                </Nav>
            </Main>
            <Menu menu={menu}>
                <a className="sizedependent" href="/login">Log In/Sign Up</a>
                <a className="sizedependent" href="/checkout"><FaShoppingCart/> Checkout</a>
                <a href="/nutritioncalc">Nutrition Calculator</a>
                <a href="/about">About</a>
                <a href="/about">About</a>
                <a href="/about">About</a>
                <a href="/about">About</a>
            </Menu>
        </Container>
    );
}

export default Header

const Container = styled.div`
    font-size: 20px;
`
const Main = styled.div`
    height: 40px;
    background: ${COLORS.mantis};
    display: flex;
    align-items: center;
    justify-content: space-between;
`

const Logo = styled.a`
    display: flex;
    align-items: center;
    text-decoration: none;
    margin: 10px;
    height: 100%;
    img {
        max-height: 100%;
        margin-right: 5px;
    }
    div {
        white-space: nowrap;
        color: black;
    }

    @media (max-width: 768px) {
        div {
            display: none;
        }
    }
`

const Search = styled.div`
    position: relative;
    flex-grow: 1;
    min-width: 270px;
    max-width: 600px;
    height: 30px;
    input {
        height: 30px;
        width: 100%;
        opacity: 1;
        border: none;
        border-radius: 3px;
        line-height: 3;
    }
    svg {
        height: 25px;
        color: #555;
        position: absolute;
        right: 5px;
        top: 2.5px;
        z-index: 1;
        cursor: text;
    }
`

const Nav = styled.nav`
    display: flex;
    align-items: center;
    > * {
        margin-right: 12px;
        margin-left: 12px;
        color: black;
    }
    a.login {
        font-size: 12px;
        text-decoration: none;
        white-space: nowrap;
        padding: 3px 8px 3px 8px;
        border: solid;
        border-width: 1px;
        border-radius: 4px;
        border-color: black;
    }
    svg {
        font-size: 20px;
        vertical-align: bottom;
        height: 25px;
        cursor: pointer;
    }

    @media (max-width: 768px) {
        a {
            display: none;
        }
    }
`

const Menu = styled.div`
    width: 300px;
    background: #68ad43;
    height: 100vh;
    display: flex;
    flex-direction: column;
    position: fixed;
    top: 40px;
    right: ${({ menu }) => (menu ? '0' : '-100%')};
    transition: 350ms;
    z-index: 10;
    a {
        margin: 5px;
        padding: 10px;
        border-bottom: solid;
        color: black;
        text-decoration: none;
        &:hover{
            background: green;
        }
    }
    @media (min-width: 768px) {
        a.sizedependent {
            display: none;
        }
    }
`
