import React, { useState } from 'react'
import styled from 'styled-components'
import { COLORS } from '../Colors.js'
import { FaBars } from 'react-icons/fa'

function Header() {
    const [click, setClick] = useState(false);
    const handleClick = () => setClick(!click)
    const [menu, setMenu] = useState(false);
    const showMenu = () => {
        if (window.innerWidth <= 800) {
            setMenu(true);
        } else {
            setMenu(false);
        }
    };
    window.addEventListener('resize', showMenu);

    return (
        <Container>
            <Main>
                <Logo href="/" >
                    <img src="logo.png" />
                    <div>Company Name</div>
                </Logo>
                <Search>
                    <input type="text" placeholder=" Search by product or location"/>
                    <img src="images/search-icon.svg"/>
                </Search>
                <Nav>
                    <a href="/singup">Sign Up</a>
                    <a href="/login">Log In</a>
                    <a href="/checkout">Checkout</a>
                    <FaBars/>
                    <img onClick={handleClick} src={click ? 'times.svg' : 'bars.svg'}/>
                </Nav>
            </Main>
            <Menu>
                <ul>
                    <li>about</li>
                    <li>services</li>
                    <li>nutrition calculator</li>
                    <li>something something</li>
                </ul>
            </Menu>
        </Container>
    );
}

export default Header

const Container = styled.div`
    font-size: 20px;
`
const Main = styled.div`
    height: 40px;
    background: ${COLORS.mantis};
    display: flex;
    align-items: center;
    justify-content: space-between;
`

const Logo = styled.a`
    display: flex;
    align-items: center;
    text-decoration: none;
    margin: 10px;
    height: 100%;
    img {
        max-height: 100%;
        margin-right: 5px;
    }
    div {
        white-space: nowrap;
        color: black;
    }
`

const Search = styled.div`
    position: relative;
    flex-grow: 1;
    min-width: 320px;
    max-width: 600px;
    height: 30px;
    input {
        height: 30px;
        width: 100%;
        opacity: 1;
        border: none;
        border-radius: 3px;
        line-height: 3;
    }
    img {
        position: absolute;
        right: 0px;
        top: 2.5px;
        z-index: 1;
        cursor: text;
    }
`

const Nav = styled.div`
    display: flex;
    align-items: center;
    a {
        margin-left: 25px;
        text-decoration: none;
        white-space: nowrap;
        color: black;
    }
    img {
        margin-left: 25px;
        margin-right: 15px;
        height: 25px;
        cursor: pointer;
    }
    svg {
        color: white;
        height: 25px;
        cursor: pointer;
    }
    * {
        padding: 3px 8px 3px 8px;
        border: solid;
        border-width: 1px;
        border-radius: 4px;
        border-color: black;
    }
`

const Menu = styled.div`
    width: 450px;
    background: grey;
`
